mapper.select:
SELECT

FROM ACT_ID_USER
WHERE ID = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE ACT_ID_USER
SET EMAIL_ = #{username, jdbcType=VARCHAR}, FIRST_ = #{password, jdbcType=VARCHAR}, LAST_ = #{email, jdbcType=VARCHAR}, PASSWORD_ = #{password, jdbcType=VARCHAR}
WHERE ID_ = #{id, jdbcType=VARCHAR}
